mapper.select:
select good_id, good_name, good_prices, good_discount, good_status, good_count, good_new, good_hot, good_level, good_brief, good_details, good_photo, c2.class2_id, c1.class1_id from class1 c1 join class2 c2 ON c1.class1_id = c2.class1_id join goods g ON g.class2_id = c2.class2_id
g.good_name like #{goodName}
and g.good_prices between #{goodsPriceMin} and #{goodsPriceMax}
and c1.class1_id=#{class1Id}
and c2.class2_id=#{class2Id}

mapper.update:
update goods set

	good_hot=#{goodHot}


	good_new=#{goodNew}


	good_status=#{goodStatus}


	good_photo=#{goodPhoto}

where good_id=#{goodId}
